declare const supplierSchema = "\n  type Supplier {\n    id: Int!\n    name: String\n    country:String\n    city:String\n    subCity:String\n    contactNumber:String\n    houseNumber:String\n    specificName:String\n    userId: Int!\n    isVerified:Boolean\n    categoryId: Int!\n    createdAt: String!\n    updatedAt: String!\n    category:Category\n  }\n\n  input CreateSupplierInput {\n    name: String\n    country:String\n    city:String\n    subCity:String\n    contactNumber:String\n    houseNumber:String\n    specificName:String\n    userId: Int!\n    categoryId: Int!\n  }\n\n  input UpdateSupplierInput {\n    name: String\n    country:String\n    city:String\n    subCity:String\n    contactNumber:String\n    houseNumber:String\n    specificName:String\n    userId: Int\n    categoryId: Int\n  }\n  \n  type Query {\n    suppliers: [Supplier!]!\n    supplier(id: Int!): Supplier\n    suppliersByCategoryId(categoryId: Int!): [Supplier!]!\n    supplierCategory:[Supplier!]!\n    supplierIdByUserId(userId: Int!): Supplier!\n  }\n\n  type Mutation {\n    createSupplier(input: CreateSupplierInput!): Supplier!\n    updateSupplier(id: Int!, input: UpdateSupplierInput!): Supplier!\n    deleteSupplier(id: Int!): Boolean!\n  }\n";
export default supplierSchema;
